private fun `solve part 2`() {
    val floorCounter = AtomicInteger(0)

    DAY_1_INPUT.forEachIndexed { index, it ->
        run {
            if (it == '(') {
                floorCounter.incrementAndGet()
            } else {
                floorCounter.decrementAndGet()
            }
            if (floorCounter.get().equals(-1)) {
                println(index + 1)
                System.exit(0)
            }
        }
    }
}

